INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Daalia', 'Dahlia x hortensis', 'Tarhadaalia on perinteinen, loppukesällä hehkuvana kukkiva kesäkukka. Daalian lajikekirjo on runsas. Kukinnan muodot vaihtelevat pallomaisista päivänkakkaramaisiin ja värivalikoima kattaa lähes kaikki värit. (lähde: Kekkilä, https://www.kekkila.fi/kasvikirjasto/tarhadaalia/', 3.50, '/static/images/daalia.jpg', 'daalia', '2021-06-22 19:10:25-07');
INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Lumme', 'Nymphaea', 'ovat vesikasveja. Niiden maavarsi ja juuristo kasvavat järven tai merenlahden pohjaan hautautuneena. Lumpeiden pitkäruotiset lehdet ja kukkavanat ulottuvat veden pinnalle. Mehevä juurakko on piisamin mieliruokaa. (lähde: wikipedia, https://fi.wikipedia.org/wiki/Lumpeet', 2.50, '/static/images/lumme.jpg', 'lumme', '2021-06-22 19:10:25-07');
