INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Marigold', 'Tagetes', 'Voimakkaasti tuoksuvat ja paahdetta sietävät samettikukat ovat suosittuja ja kestäviä kesäkukkia. Samettikukat sopivat myös kasvimaan koristukseksi, sillä ne toimivat tuholaisten karkotuskasvina. Lajikkeita on satoja erilaisia.', 1.10, '/static/images/samettikukka.jpg', 'samettikukka', '2021-06-22 19:10:25-07');
INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Dahlia', 'Dahlia x hortensis', 'Tarhadaalia on perinteinen, loppukesällä hehkuvana kukkiva kesäkukka. Daalian lajikekirjo on runsas. Kukinnan muodot vaihtelevat pallomaisista päivänkakkaramaisiin ja värivalikoima kattaa lähes kaikki värit. (lähde: Kekkilä, https://www.kekkila.fi/kasvikirjasto/tarhadaalia/', 3.50, '/static/images/daalia.jpg', 'daalia', '2021-06-22 19:10:25-07');
INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Water Lilly', 'Nymphaea', 'ovat vesikasveja. Niiden maavarsi ja juuristo kasvavat järven tai merenlahden pohjaan hautautuneena. Lumpeiden pitkäruotiset lehdet ja kukkavanat ulottuvat veden pinnalle. Mehevä juurakko on piisamin mieliruokaa. (lähde: wikipedia, https://fi.wikipedia.org/wiki/Lumpeet', 2.50, '/static/images/lumme.jpg', 'lumme', '2021-06-22 19:10:25-07');
INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Geranium', 'Pelargonium', 'Pelargonien sukuun kuuluu monenlaisia ruukkukasveja, yrttejä, varpukasveja ja pensaita, joilla kaikilla on oma ainutlaatuinen kasvutapansa ja ulkonäkönsä. (lähde: Plantagen, https://www.plantagen.fi/pelargoni.html', 3.75, '/static/images/pelargonia.jpg', 'pelargonia', '2021-06-22 19:10:25-07');
INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Verbena', 'Verbena', 'Rautayrtti on helppohoitoinen ja voimakaskasvuinen ruukku- ja amppelikasvi, joka kestää hyvin myös alhaisia lämpötiloja. (Lähde: Lepola, https://www.lepola.fi/tuotteet/kesakukat/rautayrtti', 0.90, '/static/images/rautayrtti.jpg', 'rautayrtti', '2021-06-22 19:10:25-07');
INSERT INTO flower (name, latin_name, description, price, picture, picture_alt, created_at) VALUES ('Rose', 'Loise Bugnet', 'Kaunis Kurtturuusu. Voimakas ja tuuhea ja lähes piikitön kasvusto. Punertavista nupuista kuoriutuu valkoinen tuoksuva kukka. Viihtyy niukkaravinteisessakin maassa auringosta puolivarjoon.', 6.80, '/static/images/ruusu.jpg', 'ruusu', '2021-06-22 19:10:25-07');
